import { useState } from 'react';
import './ExpenseForm.css';

const ExpenseForm = () => {
    const [userInput, setUserInput] = useState({
        userTitle: ''
    });
    const [userAmount, setUserAmount] = useState('');
    const [userDate, setUserDate] = useState('');

    const userTitleHandler = (event) => {
        setUserTitle(event.target.value);
    }

    const userAmountHandler = (event) => {
        setUserAmount(event.target.value);
    }

    const userDateHandler = (event) => {
        setUserDate(event.target.value);
    }

    return (
        <form>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text' onChange={userTitleHandler}/>
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number' min='0.01' step='0.01' onChange={userAmountHandler}/>
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='date' min='2023-01-01' onChange={userDateHandler}/>
                </div>
            </div>
            <div className='new-expense__actions'>
                <button type='submit'>Add Expense</button>
            </div>
        </form>
    )
};

export default ExpenseForm;
