import { React } from 'react';
import UserForm from './UserForm/UserForm';
import UserData from './UserData/UserData';
import User from './UserData/User.js'
import { useState } from 'react';
import ErrorModal from './ErrorModal/ErrorModal';

function App() {
  const [users, setUsers] = useState(User);

  const [isError, setError] = useState({
    error: false,
    errorMessage: ''
  })


  const onSubmitFormHandler = (u) => {
    if (u.age < 0) {
      setError(() => {
        return {
          error: true,
          errormessage: 'Invalid age input: age cannot be less than 0'
        }
      })
    }
    if (u.username === '' || u.age === '') {
      setError(() => {
        return {
          error: true,
          errormessage: 'Username or Age cannot be null'
        }
      })
    }
    if (!isError.error) {
      setUsers((userData) => {
        return [u, ...userData];
      })
    }
  } 

  return (
    <div>
      <UserForm 
        onSubmitForm={onSubmitFormHandler}
      />
      <UserData 
        users={users}
      />
      <ErrorModal/>
    </div>
  );
}

export default App;
