import { React } from 'react';
import styles from './userform.module.css';
import { useState } from 'react';

const UserForm = () => {

    const [ userData, setUserData ] = useState({
        username: '',
        age: '',
        gender: 'gender'
    });

    const onSetUserName = (event) => {
        setUserData((prevData) => {
            return { ...prevData, username: event.target.value }
        })
    }

    const onSetAge = (event) => {
        setUserData((prevData) => {
            return { ...prevData, age: event.target.value}
        })
    }

    const onSetGender = (event) => {
        setUserData((prevData) => {
            return { ...prevData, gender: event.target.value}
        })
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        return userData;
    }

    return (
        <form 
            className={styles.userform}
            onSubmit={handleSubmit}
            // onSubmitForm={onSubmitFormHandler}
            >
            <div className={styles.userprofile}>
                <label className={styles.label}>
                    Username:
                </label>
                <input 
                    className={styles.input} 
                    type='text' 
                    id='username'
                    value={userData.username}
                    onChange={onSetUserName}
                />
            </div>
            <div className={styles.userprofile}>
                <label className={styles.label}>
                    Age (Years):
                </label>
                <input 
                    className={styles.input} 
                    type='text'
                    id='age'
                    value={userData.age}
                    onChange={onSetAge}
                />
            </div>
            <select clas value={userData.gender} onChange={onSetGender}>
                <option value='male'>Male</option>
                <option value='female'>Female</option>
            </select>
            <input 
                className={styles.button}
                type='submit'
                value='Add User'
            />
        </form>
    );
}

export default UserForm;
