import { useState } from 'react';
import './ExpenseFilter.css'


const ExpenseFilter = (props) => {
    const [selectedYear, setYear] = useState('2020');

    const selectedYearHandler = (event) => {
        setYear(selectedYear);
    }

    return (
        <div className='expense-filter'>
            <div className='expense-filter__control'>
                <label>Filter By Year</label>
                <select name={props.selectedYear} onChange={selectedYearHandler} id='filter'>
                    <options value='2020'>2020</options>
                    <options value='2021'>2021</options>
                    <options value='2022'>2022</options>
                    <options value='2023'>2023</options>
                </select>
            </div>
        </div>
    )
};

export default ExpenseFilter;
